mapper.select:
SELECT id, normalized_menu_name AS normalizedMenuName, like_count AS likeCount, dislike_count AS dislikeCount
FROM menu_like_dislike
WHERE normalized_menu_name = #{normalizedMenuName}

mapper.update:
UPDATE menu_like_dislike
SET like_count = #{likeCount}
WHERE normalized_menu_name = #{normalizedMenuName}

UPDATE menu_like_dislike
SET dislike_count = #{dislikeCount}
WHERE normalized_menu_name = #{normalizedMenuName}
