mapper.select:
SELECT id, menu_date AS menuDate, menu_name AS menuName, like_count AS likeCount, dislike_count AS dislikeCount
FROM menu_like_dislike
WHERE menu_date = #{menuDate}
  AND menu_name = #{menuName}

mapper.update:
UPDATE menu_like_dislike
SET like_count = #{likeCount}
WHERE menu_date = #{menuDate}
  AND menu_name = #{menuName}

UPDATE menu_like_dislike
SET dislike_count = #{dislikeCount}
WHERE menu_date = #{menuDate}
  AND menu_name = #{menuName}
