mapper.select:
SELECT id, menu_date AS menuDate, menu_name AS menuName, vote_type AS voteType, session_id AS sessionId, created_at AS createdAt
FROM menu_vote
WHERE menu_date = #{menuDate}
  AND menu_name = #{menuName}
  AND session_id = #{sessionId}

mapper.delete:
DELETE FROM menu_vote
WHERE menu_date = #{menuDate}
  AND menu_name = #{menuName}
  AND session_id = #{sessionId}
