mapper.select:
SELECT id, normalized_menu_name AS normalizedMenuName, vote_type AS voteType, session_id AS sessionId, created_at AS createdAt
FROM menu_vote
WHERE normalized_menu_name = #{normalizedMenuName}
  AND session_id = #{sessionId}

mapper.delete:
DELETE FROM menu_vote
WHERE normalized_menu_name = #{normalizedMenuName}
  AND session_id = #{sessionId}
